// Get the incoming item from the previous node (the Webhook).
// 'items' is an array of all incoming items, and we usually work with the first one.
const item = items[0];

// This code generates a unique ID without needing any special libraries.
// It combines the current timestamp with a random string.
const sessionId = Date.now().toString(36) + Math.random().toString(36).substring(2);

// Add the new 'sessionId' field to the 'json' object of the incoming item.
// This preserves all the original data from the webhook.
item.json.sessionId = sessionId;

// Return the modified item.
// Now the output will contain both the original webhook data and the new session ID.
return item;

