// This node fetches cryptocurrency data for multiple symbols from the free CoinPaprika API.
// It does not require an API key.

// --- LOGIC ---

// Get the incoming item from the previous node.
// We expect an array of symbols, e.g., { "json": { "symbols": ["QUBIC", "BTC"] } }
const item = items[0];
const cryptoSymbols = item.json.symbols;

if (!cryptoSymbols || !Array.isArray(cryptoSymbols) || cryptoSymbols.length === 0) {
  throw new Error('No cryptocurrency symbols provided. Please provide an array of symbols in the "symbols" field.');
}

// --- Step 1: Fetch the entire coin list once to avoid multiple API calls ---
let allCoins;
try {
  console.log("Fetching the main coin list from CoinPaprika...");
  // FIX: Added 'json: true' to ensure the response is parsed as a JSON array.
  allCoins = await this.helpers.request({ uri: 'https://api.coinpaprika.com/v1/coins', json: true });
  console.log(`Found ${allCoins.length} coins.`);
} catch (error) {
  console.error("Error fetching coin list from CoinPaprika:", error.message);
  throw new Error('Could not connect to the CoinPaprika API to get the coin list.');
}

const allResults = [];

// --- Step 2: Loop through each symbol and get its data ---
for (const symbol of cryptoSymbols) {
  console.log(`Searching for symbol: ${symbol}`);

  // Find the specific coin that matches the user's symbol from the pre-fetched list
  const coinInfo = allCoins.find(c => c.symbol.toUpperCase() === symbol.toUpperCase());

  if (!coinInfo) {
    console.warn(`Could not find a coin with the symbol "${symbol}" on CoinPaprika. Skipping.`);
    continue; // Skip to the next symbol if not found
  }

  const coinId = coinInfo.id;
  console.log(`Found ID for ${symbol}: ${coinId}. Fetching ticker data...`);

  // Fetch the Ticker Data using the Coin ID
  let tickerData;
  try {
    // Also use json: true here for consistency
    tickerData = await this.helpers.request({ uri: `https://api.coinpaprika.com/v1/tickers/${coinId}`, json: true });
  } catch (error) {
    console.error(`Error fetching ticker data for ${coinId}:`, error.message);
    continue; // Skip if there's an error for this specific coin
  }

  // --- DATA PROCESSING ---
  const quote = tickerData.quotes.USD;
  const formattedData = {
    name: tickerData.name,
    symbol: tickerData.symbol,
    price: quote.price,
    price_formatted: `$${quote.price.toLocaleString('en-US', { minimumFractionDigits: 2, maximumFractionDigits: 8 })}`,
    market_cap: quote.market_cap,
    market_cap_formatted: `$${Math.round(quote.market_cap).toLocaleString('en-US')}`,
    volume_24h: quote.volume_24h,
    percent_change_24h: quote.percent_change_24h,
    last_updated: tickerData.last_updated,
    paprika_url: `https://coinpaprika.com/coin/${coinId}/`
  };

  allResults.push(formattedData);
  console.log(`Successfully fetched and formatted data for ${symbol}.`);
}

// Replace the incoming data with a new object containing all our results
item.json.coin_data = allResults;

// Return the item to be used by the next node
return item;

// Code1 ‚Äî CoinPaprika multi-coin fetch (fixed for n8n)

// Accessing input items correctly for n8n
const inputItems = $input.all() || [];
const item = inputItems[0] || { json: {} };

// Use the symbols from the first input item, or default symbols
const cryptoSymbols = Array.isArray(item.json.symbols) && item.json.symbols.length
  ? item.json.symbols
  : ["QUBIC", "BTC"];

async function httpJSON(url) {
  // Using the older helpers method which should be available
  return this.helpers.httpRequest({
    method: 'GET',
    url,
    json: true,
    headers: { Accept: 'application/json' },
  });
}

let allCoins;
try {
  // Fixed URL: Removed extra spaces
  allCoins = await httpJSON.call(this, 'https://api.coinpaprika.com/v1/coins');
} catch (err) {
  throw new Error('Could not connect to CoinPaprika /v1/coins: ' + (err.message || err));
}

const results = [];

for (const symbol of cryptoSymbols) {
  const up = String(symbol || '').toUpperCase();
  if (!up) continue;

  // Find coin by symbol in the pre-fetched list
  const coinInfo = allCoins.find(c => String(c.symbol || '').toUpperCase() === up);
  if (!coinInfo?.id) {
    results.push({ symbol: up, error: 'Symbol not found on CoinPaprika' });
    continue;
  }

  try {
    // Fixed URL: Removed extra spaces
    const t = await httpJSON.call(this, `https://api.coinpaprika.com/v1/tickers/${coinInfo.id}`);
    const usd = t?.quotes?.USD || {};
    const price = typeof usd.price === 'number' ? usd.price : null;

    const fmtUSD = (n) => (typeof n === 'number' && isFinite(n))
      ? (n < 1 ? `$${n.toFixed(8)}` : `$${n.toFixed(2)}`)
      : null;

    results.push({
      id: coinInfo.id,
      name: t?.name || coinInfo.name || up,
      symbol: t?.symbol || up,
      price,
      price_formatted: fmtUSD(price),
      market_cap: usd.market_cap ?? null,
      market_cap_formatted: typeof usd.market_cap === 'number' ? `$${Math.round(usd.market_cap).toLocaleString('en-US')}` : null,
      volume_24h: usd.volume_24h ?? null,
      percent_change_24h: usd.percent_change_24h ?? null,
      last_updated: t?.last_updated || null,
      // Fixed URL: Removed extra spaces
      paprika_url: `https://coinpaprika.com/coin/${coinInfo.id}/`,
    });
  } catch (err) {
    results.push({ symbol: up, error: `Ticker fetch failed: ${err.message || err}` });
  }
}

// Prepare the output item
const outputItem = { json: { ...item.json, coin_data: results } };

// Return a stringified array of items to avoid "Wrong output type returned" error
return JSON.stringify([outputItem]);

// Get the incoming item from the previous node (the Webhook).
// 'items' is an array of all incoming items, and we usually work with the first one.
const item = items[0];

// This code generates a unique ID without needing any special libraries.
// It combines the current timestamp with a random string.
const sessionId = Date.now().toString(36) + Math.random().toString(36).substring(2);

// Add the new 'sessionId' field to the 'json' object of the incoming item.
// This preserves all the original data from the webhook.
item.json.sessionId = sessionId;

// Return the modified item.
// Now the output will contain both the original webhook data and the new session ID.
return item;

