/**
 * n8n Code node: ComfyUI Single Image ‚Üí Video (WAN 2.2, EdgeTTS)
 *
 * Fix for "protocol mismatch": this uses plain HTTP requests to the ComfyUI REST API
 * instead of any custom ComfyUI node. Drop this into a Code node and connect it after
 * the step that produces your narration text in `item.json.output`.
 *
 * Inputs (from the current item):
 *   - json.output           ‚Üí narration text (string)
 *   - json.comfyBase        ‚Üí OPTIONAL, e.g. "http://127.0.0.1:8188"
 *
 * Output:
 *   - json.prompt_id        ‚Üí ComfyUI prompt id
 *   - json.history          ‚Üí Minimal status summary
 *   - binary.video          ‚Üí final MP4 from SaveVideo (if found)
 *   - binary.audio          ‚Üí TTS MP3 (if found)
 */

const COMFY_BASE = ($json.comfyBase || 'http://100.113.28.30:8188').replace(/\/$/, '');
const IMAGE_URI = 'DuckBotNewsAnchor7.png';

// Pull narration from upstream; fall back to a safe default
const narration = (typeof $json.output === 'string' && $json.output.trim())
  ? $json.output.trim()
  : "Good evening. Here's your latest crypto trading analysis.";

// Small helper
const sleep = (ms) => new Promise(r => setTimeout(r, ms));

// --- 1) Build the ComfyUI graph (based on your provided workflow) ---
//     - Inject Windows file URI into LoadImage
//     - Inject narration into String (node 102)
//     - Also append narration to the video positive prompt (node 46)
const graph = {
  "3": {"inputs":{"seed":1075925524038446,"steps":50,"cfg":4.98,"sampler_name":"dpmpp_3m_sde_gpu","scheduler":"exponential","denoise":1,"model":["4",0],"positive":["6",0],"negative":["7",0],"latent_image":["11",0]},"class_type":"KSampler","_meta":{"title":"Audio KSampler"}},
  "4": {"inputs":{"ckpt_name":"stable-audio-open-1.0.safetensors"},"class_type":"CheckpointLoaderSimple","_meta":{"title":"Audio Model Loader"}},
  "6": {"inputs":{"text":"professional male financial analyst voice, confident and authoritative tone, clear broadcast diction for cryptocurrency news delivery, steady measured pace, close microphone presence, subtle trading floor ambiance with distant keyboard clicks and monitor hums, no background music, duration 15‚Äì30 seconds","clip":["10",0]},"class_type":"CLIPTextEncode","_meta":{"title":"Audio Positive Prompt"}},
  "7": {"inputs":{"text":"music, melody, vocals (other than the anchor), crowd noise, sirens, traffic, phone beep, UI sounds, reverb tails longer than 1.5 s","clip":["10",0]},"class_type":"CLIPTextEncode","_meta":{"title":"Audio Negative Prompt"}},
  "10": {"inputs":{"clip_name":"t5-base.safetensors","type":"stable_audio","device":"default"},"class_type":"CLIPLoader","_meta":{"title":"Audio CLIP Loader"}},
  "11": {"inputs":{"seconds":5,"batch_size":1},"class_type":"EmptyLatentAudio","_meta":{"title":"EmptyLatentAudio"}},
  "12": {"inputs":{"samples":["3",0],"vae":["4",2]},"class_type":"VAEDecodeAudio","_meta":{"title":"VAEDecodeAudio"}},
  "13": {"inputs":{"filename_prefix":"audio/ComfyUI","audioUI":"","audio":["12",0]},"class_type":"SaveAudio","_meta":{"title":"SaveAudio"}},
  "37": {"inputs":{"unet_name":"wan2.2_ti2v_5B_fp16.safetensors","weight_dtype":"fp8_e4m3fn_fast"},"class_type":"UNETLoader","_meta":{"title":"Video Model Loader"}},
  "38": {"inputs":{"clip_name":"umt5_xxl_fp8_e4m3fn_scaled.safetensors","type":"wan","device":"cpu"},"class_type":"CLIPLoader","_meta":{"title":"Video CLIP Loader"}},
  "39": {"inputs":{"vae_name":"wan2.2_vae.safetensors"},"class_type":"VAELoader","_meta":{"title":"Video VAE Loader"}},
  "46": {"inputs":{"text":"Professional Purple Sun duck news anchor in navy business suit, confident posture at news desk, animated speaking motion with subtle head movements and beak articulation, Purple Sun cosmic logo glowing behind, multiple LCD monitors displaying Bitcoin and crypto charts, financial newsroom setting, warm studio lighting, broadcast television quality, smooth animation, duck character maintaining professional demeanor while delivering crypto trading analysis","clip":["38",0]},"class_type":"CLIPTextEncode","_meta":{"title":"Video Positive Prompt"}},
  "47": {"inputs":{"text":"cartoon, anime, CGI, game render, painterly, toy-like, distorted face, extra fingers, deformed hands, text, lower third, ticker, subtitles, logo, watermark, channel bug, UI overlay, chroma-key halo, green spill, fisheye, dutch angle, skewed horizon, lowres, blurry, oversharpen halos, banding, tiling, compression artifacts, bad perspective, heavy motion blur, ghosting, frame duplication","clip":["38",0]},"class_type":"CLIPTextEncode","_meta":{"title":"Video Negative Prompt"}},
  "48": {"inputs":{"shift":8,"model":["37",0]},"class_type":"ModelSamplingSD3","_meta":{"title":"ModelSamplingSD3"}},
  "50": {"inputs":{"seed":590958479946735,"steps":25,"cfg":3.5,"sampler_name":"uni_pc","scheduler":"kl_optimal","denoise":0.9000000000000001,"model":["48",0],"positive":["46",0],"negative":["47",0],"latent_image":["55",0]},"class_type":"KSampler","_meta":{"title":"Video KSampler"}},
  "55": {"inputs":{"width":736,"height":736,"length":121,"batch_size":1,"vae":["39",0],"start_image":["56",0]},"class_type":"Wan22ImageToVideoLatent","_meta":{"title":"Wan22ImageToVideoLatent"}},
