  "57": {"inputs":{"fps":30,"images":["84",0],"audio":["103",0]},"class_type":"CreateVideo","_meta":{"title":"Create Video"}},
  "58": {"inputs":{"filename_prefix":"video/Trading_News_Report","format":"mp4","codec":"auto","video":["57",0]},"class_type":"SaveVideo","_meta":{"title":"Save Video"}},
  "60": {"inputs":{"samples":["50",0],"vae":["39",0]},"class_type":"VAEDecode","_meta":{"title":"VAE Decode"}},
  "81": {"inputs":{"upscale_model":["82",0],"image":["60",0]},"class_type":"ImageUpscaleWithModel","_meta":{"title":"Upscale Video Frames"}},
  "82": {"inputs":{"model_name":"RealESRGAN_x4.pth"},"class_type":"UpscaleModelLoader","_meta":{"title":"Free 4x Upscaler Model (Connected)"}},
  "84": {"inputs":{"upscale_method":"lanczos","width":2560,"height":1440,"crop":"disabled","image":["81",0]},"class_type":"ImageScale","_meta":{"title":"Resolution Selector"}},
  "102": {"inputs":{"value": narration},"class_type":"String","_meta":{"title":"üìù TTS Script Input"}},
  "103": {"inputs":{"text":["102",0],"voice":"[English] en-US Andrew","speed":1,"pitch":6},"class_type":"EdgeTTS","_meta":{"title":"üé§ Generate TTS Audio"}},
  "104": {"inputs":{"filepath":"tts_narration","format":"mp3","quality":"high","overwrite":false,"audio":["103",0]},"class_type":"Save_Audio","_meta":{"title":"üíæ Save TTS Audio"}},
  "106": {"inputs":{"filepath":"environmental_audio_backup","format":"mp3","quality":"high","overwrite":false,"audio":["12",0]},"class_type":"Save_Audio","_meta":{"title":"üíæ Save Environmental Audio"}}
};

// Append narration to the video prompt to guide articulation without rendering text
graph["46"].inputs.text += `\nNarration content (verbatim, do not draw on screen): ${narration}`;

// --- 2) POST /prompt ---
const client_id = (globalThis.crypto?.randomUUID && crypto.randomUUID()) || String(Date.now());
const post = await this.helpers.httpRequest({
  method: 'POST',
  url: `${COMFY_BASE}/prompt`,
  body: { prompt: graph, client_id },
  json: true,
});

const prompt_id = post?.prompt_id;
if (!prompt_id) {
  throw new Error('ComfyUI: missing prompt_id in /prompt response');
}

// --- 3) Poll /history/{prompt_id} until completed ---
let history, tries = 0;
while (true) {
  await sleep(2000);
  history = await this.helpers.httpRequest({
    method: 'GET',
    url: `${COMFY_BASE}/history/${prompt_id}`,
    json: true,
  });
  const status = history?.[prompt_id]?.status;
  if (status?.completed) break;
  if (status?.status_str?.toLowerCase?.() === 'error') {
    throw new Error(`ComfyUI job error: ${status.status_str || 'unknown'}`);
  }
  if (++tries > 600) { // ~20 minutes max
    throw new Error('Timed out waiting for ComfyUI job');
  }
}

// --- 4) Extract result files (video & audio if present) ---
const outputs = history?.[prompt_id]?.outputs || {};
const files = [];
for (const nodeId of Object.keys(outputs)) {
  const nodeOut = outputs[nodeId] || {};
  for (const key of Object.keys(nodeOut)) {
    const list = Array.isArray(nodeOut[key]) ? nodeOut[key] : [];
    for (const obj of list) {
      if (!obj?.filename) continue;
      files.push({
        filename: obj.filename,
        subfolder: obj.subfolder || '',
        type: obj.type || 'output',
        ext: (obj.filename.split('.').pop() || '').toLowerCase(),
        fromNode: nodeId,
        key,
      });
    }
  }
}

// Helper to download a ComfyUI file
async function downloadComfy(file) {
  const url = `${COMFY_BASE}/view?filename=${encodeURIComponent(file.filename)}&type=${encodeURIComponent(file.type)}&subfolder=${encodeURIComponent(file.subfolder)}`;
  const data = await this.helpers.httpRequest({ method: 'GET', url, encoding: null }); // Buffer
  const mime = file.ext === 'mp4' ? 'video/mp4' : file.ext === 'mp3' ? 'audio/mpeg' : 'application/octet-stream';
  return this.helpers.prepareBinaryData(data, file.filename, mime);
}

const item = items[0];
item.json.prompt_id = prompt_id;
item.json.history = { ok: true, outputs_found: files.length };
item.binary = item.binary || {};

// Prefer MP4 from SaveVideo (node 58), and TTS MP3 from node 104 if present
const mp4 = files.find(f => f.ext === 'mp4') || files.find(f => f.type === 'video');
const mp3 = files.find(f => f.ext === 'mp3');

if (mp4) item.binary.video = await downloadComfy.call(this, mp4);
if (mp3) item.binary.audio = await downloadComfy.call(this, mp3);

return item;

const item = $input.first().json
return {
  json : {
    ...item,
    "errorMessage" : "Failed to get ComfyUI History LOCALLY."
  }
};

const item = $input.first().json
return {
  json : {
    ...item,
    "errorMessage" : "Failed to trigger workflow LOCALLY."
  }
};

const item = $input.first().json
return {
  json : {
    ...item,
    "errorMessage" : "Failed to upload attachments LOCALLY."
  }
};

