  const { prompt_id } = await submitGraph.call(this, base, g);
  chunkJobs.push({ index:i, base, prompt_id, frames:framesThis });
}

// 3) Concurrently poll jobs to completion (bounded concurrency)
async function collectOne(job){
  const out = await pollDone.call(this, job.base, job.prompt_id);
  const files = filesFromOutputs(out);
  const mp4 = files.find(f => f.fromNode==='69' && f.ext==='mp4') || files.find(f => f.ext==='mp4');
  if (!mp4) throw new Error(`Chunk ${job.index}: no MP4 from ${job.base}`);
  return {
    index: job.index, frames: job.frames, base: job.base,
    filename: mp4.filename, subfolder: mp4.subfolder || '',
    type: mp4.type || 'output', ext: mp4.ext,
    view_url: viewUrl(job.base, mp4),
  };
}

async function pMap(list, limit, fn){
  const out = new Array(list.length);
  let i=0, active=0, rej=null;
  return await new Promise((resolve, reject)=>{
    const kick = () => {
      if (rej) return;
      while (active<limit && i<list.length){
        const idx=i++, v=list[idx]; active++;
        Promise.resolve(fn(v)).then(
          (res)=>{ out[idx]=res; active--; (i>=list.length && active===0)?resolve(out):kick(); },
          (err)=>{ rej=err; reject(err); }
        );
      }
    };
    kick();
  });
}

const segments = (await pMap(chunkJobs, Math.max(1, CFG.pollConcurrency), (j)=>collectOne.call(this, j)))
  .sort((a,b)=>a.index-b.index);

